(62, 1, '2017-08-30 19:41:41', '2017-08-30 16:41:41', '<div class="style_text">\r\n<div class="address_text"><h4>Mail: <a href="mailto:freelan.len@yandex.ru">freelan.len@yandex.ru</a> , <a href="mailto:mail@lvvpc">mail@lvvpc</a></h4>\r\n<h4>Телефон: +7 (951) 527-47-12</h4>\r\n<h4>Skype: len-it-dev</h4>\r\n<h4>Контактное лицо: Владимир Лень</h4></div>\r\n\r\n<div class="address_text info">[si-contact-form form=\'1\']</div>\r\n</div>', 'Контакты', '', 'publish', 'closed', 'closed', '', '%d0%ba%d0%be%d0%bd%d1%82%d0%b0%d0%ba%d1%82%d1%8b', '', '', '2017-11-17 22:28:45', '2017-11-17 19:28:45', '', 5, 'http://lvvpc:8080/portfol/?page_id=62', 0, 'page', '', 0),
